import Carousel from 'react-bootstrap/Carousel';
import styled from 'styled-components';
const StyledCarouselImage = styled.img`
    width: 100%;
    height: auto;
    max-height: 60vh;
    object-fit: cover;
`;
const StyledCarouselCaption = styled.div`
    background-color: rgba(0, 0, 0, 0.6);
    color: white;
    padding: 20px;
    border-radius: 10px;
`;

const carouselData = [
    {
        image: "https://images.pexels.com/photos/5644378/pexels-photo-5644378.jpeg?auto=compress&cs=tinysrgb&w=800",
        heading: "Welcome",
        caption: "Some representative placeholder content for the first slide.",
      },
      {
        image: "https://images.pexels.com/photos/1447000/pexels-photo-1447000.jpeg?auto=compress&cs=tinysrgb&w=800&lazy=load",
        heading: "Hello",
        caption: "Some representative placeholder content for the first slide.",
      },
      {
        image: "https://images.pexels.com/photos/2292976/pexels-photo-2292976.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=2",
        heading: "Hello",
        caption: "Some representative placeholder content for the first slide.",
      }

]

function CarouselImage({ image, heading, caption }) {
    return (
        <Carousel.Item key={image}>
        <img src={image} className="d-block w-100" alt="..." />
        <Carousel.Caption>
            <h1>{heading}</h1>
            <p>{caption}</p>
        </Carousel.Caption>
        </Carousel.Item>
    );
    }



export default function WelcomeCarousel () {
    return (
        <Carousel className="d-none d-sm-block">
        {carouselData.map(CarouselImage)}
    </Carousel>
    );
}
