//Centralized State Management: Use instead of passing the setIsLoggedIn function down through multiple levels of components.

import React, { createContext, useState, useContext } from 'react';

const AuthContext = createContext();

//The useAuth function is a custom hook that makes it easier to access the authentication context. Custom hooks are reusable functions that encapsulate some logic and can be called from functional components.
export const useAuth = () => {
    const context = useContext(AuthContext);
    if (!context) {
        throw new Error('useAuth must be used within an AuthProvider');
    }
    return context;
};


//The AuthProvider is a React component that uses the Context API to provide authentication-related data (in this case, whether the user is logged in and a function to change that state) to any child components that need it.
export const AuthProvider = ({ children }) => {
    const [isLoggedIn, setIsLoggedIn] = useState(
        localStorage.getItem("token") ? true : false
    );

    return (
        <AuthContext.Provider value={{ isLoggedIn, setIsLoggedIn }}>
            {children}
        </AuthContext.Provider>
    );
};
