import { Form, Button, Container } from 'react-bootstrap';

export default function SignInForm () {
        const formStyle = {
            backgroundImage: 'url("https://your-background-image-url.jpg")', // Replace with your image URL
            backgroundSize: 'cover',
            backgroundPosition: 'center',
            height: '50vh', // Make it full height of the viewport
            display: 'flex',
            alignItems: 'center', // Center vertically
            justifyContent: 'center', // Center horizontally
            position: 'relative',
        };
    
        const overlayStyle = {
            backgroundColor: 'rgba(0, 0, 0, 0.5)', // Dark transparent background
            padding: '20px',
            borderRadius: '8px',
            width: '100%',
            maxWidth: '400px', // Limit form width
        };

        return (
            <div style={formStyle}>
                <div style={overlayStyle}>
                    <h2 style={{ color: 'white', textAlign: 'center' }}>Sign In</h2>
                    <Form>
                        <Form.Group controlId="formBasicEmail" className="mb-3">
                            <Form.Label>Email address</Form.Label>
                            <Form.Control type="email" placeholder="Enter email" />
                        </Form.Group>
                        <Form.Group controlId="formBasicPassword">
                            <Form.Label>Password</Form.Label>
                            <Form.Control type="password" placeholder="Password" />
                        </Form.Group>
                        <Button variant="primary" type="submit" block>
                            Sign In
                        </Button>
                    </Form>
                </div>
            </div>
    );
}
