import { Form, Button, Container } from 'react-bootstrap';

export default function SignInForm () {
        const formStyle = {
            backgroundImage: 'url("https://your-background-image-url.jpg")', // Replace with your image URL
            backgroundSize: 'cover',
            backgroundPosition: 'center',
            height: '100vh', // Make it full height of the viewport
            display: 'flex',
            alignItems: 'center', // Center vertically
            justifyContent: 'center', // Center horizontally
            position: 'relative',
        };
    
        const overlayStyle = {
            backgroundColor: 'rgba(0, 0, 0, 0.5)', // Dark transparent background
            padding: '20px',
            borderRadius: '8px',
            width: '100%',
            maxWidth: '400px', // Limit form width
        };

        return (
            <Form>
            <Form.Group className="mb-3" controlId="formBasicEmail">
            <Form.Label>Organisation</Form.Label>
            <Form.Control type="name" placeholder="Enter organisation" />
            <Form.Text className="text-muted">
            </Form.Text>
            </Form.Group>
            <Form.Group className="mb-3" controlId="formBasicPassword">
            <Form.Label>Password</Form.Label>
            <Form.Control type="password" placeholder="Password" />
            </Form.Group>
            <Button variant="primary" type="submit">
            Sign In
            </Button>
        </Form>
    );
}
