import { Form, Button, Container } from 'react-bootstrap';
import images from '../Assets/images';
import React, { useState } from 'react';
import { useAuth } from '../AuthContext';
import './UserWelcome.styles.css'; 

export default function UserWelcome () {
    const { 
        username

     } = useAuth();
    console.log('Current username:', username);

    const locations = [
        'Cairns',
        'Noosa',
        'Gold Coast',
        'Whitsunday'
      ];

    // Set initial location based on user
    useEffect(() => {
        const userLocations = {
            'admin': 'Cairns',
            'user1': 'Noosa'
        };
        setSelectedLocation(userLocations[username] || locations[0]);
    }, [username]);

    const handleLocationChange = (e) => {
        setSelectedLocation(e.target.value);   
    };

    return (
        <div className="background"
        style={{ backgroundImage: `url(${images.seaRocks01})` }}>

            <div className='overlay'>
                <h2 style={{ color: 'white' }}>Welcome '{username}' from </h2>
                        <Form.Select
                        value={selectedLocation}
                        onChange={handleLocationChange}
                        className="bg-white/90 backdrop-blur-sm"
                        >
                        {locations.map((location) => (
                        <option key={location} value={location}>
                            {location}
                        </option>
                        ))}
                    </Form.Select>
            </div>
        </div>
                

    )

}
